import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';
import { LibrarylogService } from '../librarylog.service';

@Component({
  selector: 'app-signup',
  templateUrl: './signup.component.html',
  styleUrls: ['./signup.component.css']
})
export class SignupComponent implements OnInit {
librarylogForm = new FormGroup({
  lastname : new FormControl(null),
  firstname : new FormControl(null),
  middlename : new FormControl(null),
  birthdate : new FormControl(null),
  email : new FormControl(null),
  password : new FormControl(null)
})

  constructor(private post : LibrarylogService) { }

  ngOnInit(): void { }

  SaveFunct(){
    //console.log(this.librarylogForm.value);

    this.post.saveService(this.librarylogForm.value)
      .subscribe((result:any)=>{
        console.log(result)
      })
  }
}
